df = pd.DataFrame(data)

st.title("Fortnite Creative Map Analytics Dashboard")

# Filters
selected_genre = st.selectbox("Select Genre", ["All"] + sorted(df['Genre'].unique()))
selected_creator = st.selectbox("Select Creator Type", ["All"] + sorted(df['Creator Type'].unique()))

filtered_df = df.copy()
if selected_genre != "All":
    filtered_df = filtered_df[filtered_df["Genre"] == selected_genre]
if selected_creator != "All":
    filtered_df = filtered_df[filtered_df["Creator Type"] == selected_creator]

# Display table
st.subheader("Island Metrics")
st.dataframe(filtered_df)

# Charts
st.subheader("Plays by Genre")
st.bar_chart(df.groupby("Genre")["Plays"].sum())

st.subheader("Average Minutes per Player by Creator Type")
st.bar_chart(df.groupby("Creator Type")["Avg Minutes per Player"].mean())

# Export options
st.subheader("Export Data")
export_format = st.radio("Choose export format", ["Excel", "CSV"])
if export_format == "Excel":
    output = io.BytesIO()
    with pd.ExcelWriter(output, engine='openpyxl') as writer:
        filtered_df.to_excel(writer, index=False)
    st.download_button("Download Excel", data=output.getvalue(), file_name="fortnite_dashboard_filtered.xlsx")
else:
    csv = filtered_df.to_csv(index=False).encode('utf-8')
    st.download_button("Download CSV", data=csv, file_name="fortnite_dashboard_filtered.csv")
